# --- q1
def count_handles(journalists_array)
  journalists_array_length = journalists_array.length

  return journalists_array_length
end

# # ---q2

def find_shortest_handle(journalists_array)
  shortest_handle = journalists_array[1]

  for i in journalists_array
    if shortest_handle.length > i.length
      shortest_handle = i
    end
  end

  return shortest_handle
end


# # ---q3

def count_handles_with_length_5(journalists_array)
  length_is_5_counter = 0
  
  for i in journalists_array
    if i.length == 6
      length_is_5_counter += 1
    end
  end

  return length_is_5_counter
end

# ---q4

def how_many_start_with_caps(journalists_array, character_to_find)
  starts_with_caps_counter = 0

  for str in journalists_array
    if str.match(/(#{character_to_find}[A-Z])/)
      starts_with_caps_counter += 1
    end
  end

  return starts_with_caps_counter
end

# --- q5

def sort_my_array_alphabetical(journalists_array)
  for str in journalists_array
  sorted_array_alphabetical = journalists_array.sort
  end
  return sorted_array_alphabetical
end


# --- q6
def sort_my_array_by_handle_size(journalists_array)
   sorted_array_by_handle_size = journalists_array.sort_by {|str| str.length}
  
  return sorted_array_by_handle_size
end


# --- q7
def find_position_of_contact(journalists_array)
  # name = "@epenser"
  position_in_list = journalists_array.index {|name| name == "@epenser"}

  return position_in_list
end


# --- q8
def count_contacts_length(journalists_array)
  possible_lengths = journalists_array.map{|word| word.length}
  min = possible_lengths.min
  max = possible_lengths.max

  (min..max).each do |length|
      nombre = journalists_array.count {|word| word.length == length}
      nombre != 0 ? (puts "Le nombre de contacts dans notre array dont la longueur est égale à #{length} de caractères est de #{nombre} ") : false
  end
end

